// Get the header element
const header = document.querySelector('.sticky-header');

// Function to handle scroll events
function handleScroll(entries, observer) {
  entries.forEach(entry => {
    if (entry.isIntersecting) {
      // If the header is in view, add the "sticky" class to make it sticky
      header.classList.add('sticky');
    } else {
      // Otherwise, remove the "sticky" class to make it non-sticky
      header.classList.remove('sticky');
    }
  });

  // Update the background color based on the scroll position
  const scrollPosition = window.scrollY;
  const threshold = 200; // Change this value to adjust when the background changes
  if (scrollPosition > threshold) {
    header.style.backgroundColor = '#000'; // Change to the desired background color
  } else {
    header.style.backgroundColor = 'transparent'; // Change to the default background color
  }
}

// Create an intersection observer to track the header element
const observer = new IntersectionObserver(handleScroll, { threshold: 0 });

// Observe the header element
observer.observe(header);
